result:
.contact{
  display:grid;
  grid-template-columns: 90%;
  align-content: center;
  justify-content: center;
}
input[type = text]{
  grid-area: text;
  font-size: $Fsize;
  background: none;
  border-bottom: 1px solid $backgroundColor;
  border-top: none;
  border-left: none;
  border-right: none;
  color: $textColor;
  &:focus{
    outline: none;
    border-bottom: 1px solid $textColor;
  }
  &:focus ~ .user{
    background-color: $textColor
  }
}
input[type = email]{
  font-size: $Fsize;
  grid-area: email;
  background: none;
  border-bottom: 1px solid $backgroundColor;
  border-top: none;
  border-left: none;
  border-right: none;
  color: $textColor;
  &:focus{
    outline: none;
    border-bottom: 1px solid $textColor;
  }
  &:focus ~ .mail{
    background-color: $textColor
  }
}
textarea{
  grid-area: textarea;
  font-size: $Fsize;
  background: none;
  border-bottom: 1px solid $backgroundColor;
  border-top: none;
  border-left: none;
  border-right: none;
  color: $textColor;
  &:focus{
    outline: none;
    border-bottom: 1px solid $textColor;
  }
  &:focus ~ .msg{
    background-color: $textColor
  }
}
button{
  grid-area: button;
  border-radius: 30px;
  background-color: $backgroundColor;
  color: $textColor;
  font-size: 15px;
  font-weight: 900;
  &:focus{
    outline: none
  }
}
.user{
  background-color: $backgroundColor;
  grid-area: user;
  width: 40%;
  justify-self:end;
}
.mail{
  background-color: $backgroundColor;
  grid-area: mail;
  width: 40%;
  justify-self:end;
}
.msg{
  background-color: $backgroundColor;
  grid-area: msg;
  width: 40%;
  justify-self:end;
}
.form{
  grid-template-columns: repeat(7,1fr);
  grid-auto-rows: minmax(40px ,auto);
  display: grid;
  grid-template-areas: '. user text text text . .'
                        '. mail email email email . .'
                        '. msg textarea textarea textarea . .'
                        '. . textarea textarea textarea . .'
                        '. . textarea textarea textarea . .'
                        '. . textarea textarea textarea . .'
                        '. . . button . . .';
  align-content: center;
  grid-gap: 10px;
}
